import{c as e}from"./app.21a73593.js";import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";var a="/assets/campaign_image1.fd702e83.png",s="/assets/campaign_image2.f97fe284.png",i="/assets/campaign_image3.6efc4a66.png",n="/assets/campaign_image4.1dbb8933.png",p="/assets/campaign_image5.0d493a0e.png",o="/assets/campaign_image6.affe3a28.png",r="/assets/campaign_image7.ce46fb70.png";const c={},g=e('<h1 id="how-to-send-out-a-campaign" tabindex="-1"><a class="header-anchor" href="#how-to-send-out-a-campaign" aria-hidden="true">#</a> How to send out a campaign</h1><p>Learn how to setup and send out campaigns</p><p>To start a new campaign go to <em>Campaigns &gt; All</em> and click the red plus button in the lower right corner and you\u2019ll be forwarded to the first step of setting up a campaign.</p><p><img src="'+a+'" alt="Create new campaign"></p><p><strong>First step:</strong> type in campaign name and select the mail account from which emails will be sent and click Next to proceed.</p><p><strong>Second step:</strong> you need to provide a list of recipients. It can be done either by uploading a file with the recipients list or typing in the list of recipients - don\u2019t forget to click <em>Import</em> afterwards! This step can be skipped and you can go to the other step by clicking Next. But note that you\u2019ll not be able to send out the campaign without recipients added.</p><p><img src="'+s+'" alt="Recipients upload"></p><p><strong>Third step:</strong> set up a message by typing in the subject line and message text. If you want to use your previously created template or use one of our prepared templates - just click on <em>Templates</em>.</p><p><strong>Note:</strong> Custom Variables such as recipient name, last name, Company name and unsubscribe link can be added to the message in this step.</p><p>Additionally, <strong>follow up emails</strong> can be set in this step. Click on <em>Add follow-up</em> to pick a delay and add another email to the flow.</p><p><img src="'+i+'" alt="Add follow-up"></p><p><strong>Fourth step:</strong> a preview of how the campaign will look to the recipients. You can check the message for each recipient individually and also edit the message text.</p><p><img src="'+n+'" alt="Edit user follow-up"></p><p><strong>Fifth step:</strong> the final settings of the campaign. Select when the messages will be stopped being sent to the recipient, select a schedule template or create a new schedule template.</p><p>After checking the checkbox and familiarizing with the sending terms click Next to finalize the setup.</p><p><img src="'+p+'" alt="Campaign sending schedule"></p><p><strong>Last step:</strong> a summary of the setup. Click <em>Start your campaign</em> to send it out right away or save the setup as a draft by clicking <em>Save as draft</em>.</p><p>Once the campaign has started you can always <strong>pause</strong> it in the <em>All campaigns</em> page by clicking the pause sign next to the campaign. And likewise you can <strong>resume</strong> the campaign by clicking the start sign.</p><p><img src="'+o+'" alt="Campaigns list"><img src="'+r+'" alt="Campaigns list"></p>',19);function m(l,d){return g}var f=t(c,[["render",m]]);export{f as default};
